    def __init__(self):
        self.stack1 = []  # This stack will hold the first-in elements
        self.stack2 = []  # This stack will hold the last-in elements

    def enqueue(self, item):  
        """Add an element to the end of the queue."""
        self.stack1.append(item)

    def dequeue(self):
        """Remove and return the first element in the queue."""
        if not self.stack2:  # If stack2 is empty, copy stack1 to stack2
            while self.stack1:
                self.stack2.append(self.stack1.pop())
        return self.stack2.pop()

