import { Buffer } from 'buffer'

class TCP {
    constructor() {
        this.syn = false
        this.seq = 0
        this.ack = false
        this.ackNum = 0
        this.payload = new Uint8Array()
    }

    parse(buffer: Buffer) {
        // Parse tcp header fields
        this.syn = buffer.readUInt8(0) & 2 > 0 
        this.seq = buffer.readUInt16LE(2)
        this.ack = buffer.readUInt8(4) & 16 > 0
        this.ackNum = buffer.readUInt16LE(6)

        // Read payload
        this.payload = new Uint8Array(buffer.slice(8))
    }

    serialize() {
        const buffer = new Buffer(8 + this.payload.length)

        // Write tcp header fields
        buffer.writeUInt8(this.syn ? 2 : 0, 0)
        buffer.writeUInt16LE(this.seq, 2) 
        buffer.writeUInt8(this.ack ? 16 : 0, 4)
        buffer.writeUInt16LE(this.ackNum, 6)

        // Copy payload into buffer
        buffer.copy(this.payload, 8)

        return buffer
    }
}
```

