kernel void gaussian_blur(texture2d<half, access::read> input [[ texture(0) ]], texture2d<half, access::write> output [[ texture(1) ]],
                         uint2 gid [[ thread_position_in_grid ]]) {
    const half4 color = input.read(gid);

    // Calculate mean of Gaussian function
    float mean = 1.0 / 16.0;

    // Define standard deviation
    float stddev = 1.5;

    // Set up weights for Gaussian function
    float weights[] = { 1.0, 2.0, 1.0, 2.0, 1.0, 4.0, 2.0, 1.0 };

    float sum = 0.0;

    // Compute weighted average of neighboring pixels
    for (int i = 0; i < 8; ++i) {
        float dx = float(i) - 3.5;
        float weight = exp(-dx * dx / (2 * stddev * stddev));
        float x = float(gid.x) + dx * 2.0;
        float y = float(gid.y);
        half value = weights[i] * input.read(x, y).r;
        sum += value;
    }

    // Add bias to avoid underflow
    color.r = half(mean * sum + 0.01);

    output.write(color, gid);
}
```

