function findValue(json, key) {
    let result = [];
    function search(obj){
        if (typeof obj === 'object') {
            Object.keys(obj).forEach((k) => {
                if (k === key) {
                    return [obj[key], [k]];
                }
                if (typeof obj[k] === 'object') {
                    let res = search(obj[k]);
                    if (res) {
                        return [res[0], [k, ...res[1]]];
                    }
                }
            });
        }
    }
    search(json);
    return result;
}
